mapper.select:
SELECT *
	FROM BOARD b
	WHERE b.BOARDID = #{boardID}

SELECT i.IMAGEID, i.IMAGEPATH
	FROM IMAGE i
	WHERE i.BOARDID = #{boardID}

mapper.update:
UPDATE BOARD b
	SET
		b.BOARDTITLE = #{boardTitle},
		b.BOARDCONTENT = #{boardContent},
		b.CATEID = #{cateID}
WHERE b.BOARDID = #{boardID}

UPDATE IMAGE i


			i.IMAGEPATH = #{image}


WHERE i.BOARDID = #{boardID}		
AND i.IMAGETYPE = #{imageType}
